static long scl_factor;
static long offx;
static long offy;

// The drawing area for vectors is 358x445 starting at (offx, offy)
void osint_render(){
u32 i, j, v, c;

if (optVtxCustomColor[0]) c = RGBA(optVtxCustomColor[1], optVtxCustomColor[2], optVtxCustomColor[3], 255);
else c = (vectors_draw[0].color * 256 / VECTREX_COLORS*0x1010100)+0xFF;

		if(persFull) free(vectors_pers[persCycle]); //Free the current position pointer, which is the oldest frame
		
		vectors_pers[persCycle] = (wii_vector_t*) malloc (sizeof(wii_vector_t)*vector_draw_cnt); //allocate memory to hold the coordinates of every vector in the current frame
		vector_pers_cnt[persCycle] = vector_draw_cnt; //Remember the number of vectors drawn at this frame

		//Store the coordinates of the current frame in the persistence array
		//These are used from now (to avoid computing them again)
		for(v = 0; v < vector_draw_cnt; v++){
				vectors_pers[persCycle][v].x0 =  offx + vectors_draw[v].x0 / scl_factor;
				vectors_pers[persCycle][v].y0 =  offy + vectors_draw[v].y0 / scl_factor;
				vectors_pers[persCycle][v].x1 =  offx + vectors_draw[v].x1 / scl_factor;
				vectors_pers[persCycle][v].y1 =  offy + vectors_draw[v].y1 / scl_factor;
		}

		//This draws the old vectors [persistence]
		if(optPersistence[0] && persFull)
			for(i = 1; i <= optPersistence[1]; i++){
			j = (persCycle >= i ? persCycle - i : PERSFRAMES - i + persCycle + 1);
				for(v = 0; v < vector_pers_cnt[j]; v++){
					GRRLIB_Plot(vectors_pers[j][v].x0, vectors_pers[j][v].y0, RGBA(optPersistence[2], optPersistence[2], optPersistence[2], optPersistence[3]));
					GRRLIB_Line(vectors_pers[j][v].x0, vectors_pers[j][v].y0, vectors_pers[j][v].x1, vectors_pers[j][v].y1, RGBA(optPersistence[2], optPersistence[2], optPersistence[2], optPersistence[3]));
				}
			}

//Blurred vectors to give a glowing effect
		if(optGlow[0]){
			GRRLIB_SetBlend(GRRLIB_BLEND_ADD);
			for(v = 0; v < vector_draw_cnt; v++){
				blurDot(vectors_pers[persCycle][v].x0, vectors_pers[persCycle][v].y0, optGlow[1]/7 + 2, optGlow[1], c);
				blurDot(vectors_pers[persCycle][v].x1, vectors_pers[persCycle][v].y1, optGlow[1]/7 + 2, optGlow[1], c);
				blurLine(vectors_pers[persCycle][v].x0, vectors_pers[persCycle][v].y0, vectors_pers[persCycle][v].x1, vectors_pers[persCycle][v].y1, optGlow[1], c);
			}
			GRRLIB_SetBlend(GRRLIB_BLEND_ALPHA);
		}

		//Draw the current vectors normally
			for(v = 0; v < vector_draw_cnt; v++){
				GRRLIB_Plot(vectors_pers[persCycle][v].x0, vectors_pers[persCycle][v].y0, c);			
				GRRLIB_Line(vectors_pers[persCycle][v].x0, vectors_pers[persCycle][v].y0, vectors_pers[persCycle][v].x1, vectors_pers[persCycle][v].y1, c);
			}

		if (++persCycle > PERSFRAMES) {persCycle = 0; persFull = 1;} //wrap around and set the flag to start freeing old data

		if(overlay != NULL && optOverlay[0]) GRRLIB_DrawImg(126, 0, overlay, 0, 1, 1, 0xFFFFFF00+optOverlay[1]);
		else GRRLIB_Rectangle(offx, offy, 358, 445, c, 0);

	GRRLIB_Render();
}

